/* jshint node: true */

"use strict";

var bcrypt = require('bcryptjs');
var mongoose = require('mongoose'),
    Schema = mongoose.Schema;

var UserSchema = new Schema({
    username: {
        type: String,
        required: true,
        unique: true,
        lowercase: true
    },
    firstname: {
        type: String,
        required: true,
        lowercase: true
    },
    lastname: {
        type: String,
        required: true,
        lowercase: true
    },
    password: {
        type: String,
        required: true
    },
    role: {
        type: String,
        enum: ['student', 'instructor', 'admin'],
        default: 'student'
    },
    photo: {
        type: String,
        default: 'data:image/jpeg;base64,'
    },
    deactivated: {
        type: Boolean,
        default: false
    },
    register_status: {
        type: String,
        enum: ['pending', 'complete'],
        default: 'complete'
    },
    pre_register_key: {
        type: String
    },
    pre_registered: {
        password: String,
        course_id: String,
        section_id: String
    },
});

UserSchema.methods.encryptPassword = function(password) {
    this.password = bcrypt.hashSync(password, bcrypt.genSaltSync(10));
};

module.exports = mongoose.model('User', UserSchema);
